import { PLAY_SONG } from '../actions/index'

const initialState = [
  {
    song: undefined,
    playing: false
  }
]

function app (state, action) {
  switch (action.type) {
    case PLAY_SONG:
      return Object.assign({}, state, {
        song: action.songName

      });
    default:
      return state;
  }
};

export default app;
